SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_lote_utilizado (nr_seq_lote_p bigint) RETURNS bigint AS $body$
DECLARE


qt_material_dev_w			double precision;
qt_material_pac_w			double precision;
qt_material_req_w			double precision;
qt_total_w			double precision;



BEGIN

select	coalesce(sum(a.qt_material), 0)
into STRICT	qt_material_dev_w
from	item_devolucao_material_pac a
where	a.nr_seq_lote_fornec	= nr_seq_lote_p;

select	coalesce(sum(a.qt_material), 0)
into STRICT	qt_material_pac_w
from	material_atend_paciente a
where	a.nr_seq_lote_fornec  = nr_seq_lote_p
and	obter_se_tipo_baixa_estoque(a.nr_seq_tipo_baixa) = 'S';

select	coalesce(sum(a.qt_material_atendida),0)
into STRICT	qt_material_req_w
from	item_requisicao_material a
where	a.nr_seq_lote_fornec = nr_seq_lote_p;

qt_total_w := qt_material_pac_w + qt_material_req_w - qt_material_dev_w;

return	qt_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_lote_utilizado (nr_seq_lote_p bigint) FROM PUBLIC;

