CREATE OR REPLACE FUNCTION obter_qt_material_kit ( cd_material_p bigint, nr_seq_kit_estoque_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


ie_disp_reg_kit_estoque_w	varchar(1);
qt_material_kit_w		bigint := 0;


BEGIN

select	ie_disp_reg_kit_estoque
into STRICT	ie_disp_reg_kit_estoque_w
from	parametro_estoque
where	cd_estabelecimento = cd_estabelecimento_p;

if (cd_material_p > 0) and (coalesce(ie_disp_reg_kit_estoque_w,'N') = 'S') then
	select	sum(qt_material)
	into STRICT	qt_material_kit_w
	from	kit_estoque_comp
	where	nr_seq_kit_estoque = nr_seq_kit_estoque_p
	and	cd_material = cd_material_p;
end if;

return	qt_material_kit_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
