SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_mat_integracao_opme ( nr_seq_agenda_p bigint, nr_seq_opme_p bigint, ie_quantidade_p text) RETURNS bigint AS $body$
DECLARE


qt_material_w		double precision;

/*
ie_quantidade_p
U = Utilizado
F = Faturado*/
BEGIN


if (ie_quantidade_p = 'U') then

	select	sum((
		select	coalesce(sum(x.qt_material), 0)
		from	material_atend_paciente x
		where	a.cd_material		= x.cd_material_exec
		--and	a.cd_cgc		= x.cd_cgc_fornecedor
		and	x.nr_atendimento	= b.nr_atendimento
		and	x.nr_cirurgia = b.nr_cirurgia
		and	x.nr_seq_tipo_baixa in (
			select	nr_seq_tipo_baixa
			from	regra_qt_opme_material
			where	ie_utilizado = 'S')))
	into STRICT	qt_material_w
	from	agenda_pac_opme a,
		agenda_paciente b
	where	b.nr_sequencia = a.nr_seq_agenda
	and	(b.ie_opme_integracao IS NOT NULL AND b.ie_opme_integracao::text <> '')
	and	a.ie_integracao = 'S'
	and	b.nr_sequencia = nr_seq_agenda_p
	and	a.nr_sequencia = nr_seq_opme_p;

elsif (ie_quantidade_p = 'F') then

	select	sum((
		select	coalesce(sum(x.qt_material), 0)
	  	from	material_atend_paciente x
		where	a.cd_material		= x.cd_material_exec
		--and	a.cd_cgc		= x.cd_cgc_fornecedor
		and	x.nr_atendimento	= b.nr_atendimento
		and	x.nr_cirurgia = b.nr_cirurgia
		and	x.nr_seq_tipo_baixa in (
			select	nr_seq_tipo_baixa
			from	regra_qt_opme_material
			where	ie_faturado = 'S'))) +
		sum((
		select	coalesce(sum(t.qt_baixa_nconta), 0)
		from	prescr_material t,
			prescr_medica x
		where	x.nr_prescricao		= t.nr_prescricao
		and	x.nr_cirurgia		= b.nr_cirurgia
		and	x.nr_seq_agenda		= a.nr_seq_agenda
		and 	obter_prescr_cir_especial(b.nr_cirurgia) = x.nr_prescricao
		and	t.cd_material		= a.cd_material)) +
		sum((
		select	coalesce(sum(t.qt_baixa_especial), 0)
		from	prescr_material t,
			prescr_medica x
		where	x.nr_prescricao		= t.nr_prescricao
		and	x.nr_cirurgia		= b.nr_cirurgia
		and	x.nr_seq_agenda		= a.nr_seq_agenda
		and 	obter_prescr_cir_especial(b.nr_cirurgia) = x.nr_prescricao
		and	t.cd_material		= a.cd_material))
	into STRICT	qt_material_w
	from	agenda_pac_opme a,
		agenda_paciente b
	where	b.nr_sequencia = a.nr_seq_agenda
	and	(b.ie_opme_integracao IS NOT NULL AND b.ie_opme_integracao::text <> '')
	and	a.ie_integracao = 'S'
	and	b.nr_sequencia = nr_seq_agenda_p
	and	a.nr_sequencia = nr_seq_opme_p;


end if;

return	qt_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
