SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_mat_oc (cd_material_p bigint) RETURNS bigint AS $body$
DECLARE

 
ds_retorno_w   bigint;
			

BEGIN 
 
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then 
	select	coalesce(sum(c.qt_prevista_entrega - coalesce(c.qt_real_entrega,0)),0) 
	into STRICT 	ds_retorno_w 
	from	ordem_compra a, 
		ordem_compra_item b, 
		ordem_compra_item_entrega c 
	where	a.nr_ordem_compra = b.nr_ordem_compra 
	and	b.nr_ordem_compra = c.nr_ordem_compra 
	and	b.nr_item_oci = c.nr_item_oci 
	and	coalesce(a.dt_baixa::text, '') = '' 
	and	coalesce(a.nr_seq_motivo_cancel::text, '') = '' 
	and	coalesce(c.dt_cancelamento::text, '') = '' 
	and	a.cd_estabelecimento = 1 
	and	b.cd_material = cd_material_p;
end if;	
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_mat_oc (cd_material_p bigint) FROM PUBLIC;

