SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_mat_original ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_devolucao_w 	double precision;
qt_material_w	double precision;


BEGIN

select	coalesce(sum(qt_devolucao),0)
into STRICT	qt_devolucao_w
from	prescr_material_devolucao
where	nr_prescricao 		= nr_prescricao_p
and	nr_seq_prescricao 	= nr_sequencia_p;

select	coalesce(sum(qt_material),0)
into STRICT	qt_material_w
from	prescr_material
where	nr_prescricao 	= nr_prescricao_p
and	nr_sequencia 	= nr_sequencia_p;

return 	qt_devolucao_w + qt_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
