SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_mat_util ( nr_seq_mat_autor_cirurgia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_utilizada_w		bigint;
nr_seq_autorizacao_w	bigint;
cd_material_w		bigint;
cd_convenio_w		bigint;
nr_atendimento_w		bigint;


BEGIN

select	max(nr_seq_autorizacao),
	max(cd_material)
into STRICT	nr_seq_autorizacao_w,
	cd_material_w
from	material_autor_cirurgia
where	nr_sequencia	= nr_seq_mat_autor_cirurgia_p;

select	max(obter_convenio_atendimento(nr_atendimento)),
	max(nr_atendimento)
into STRICT	cd_convenio_w,
	nr_atendimento_w
from	autorizacao_cirurgia
where	nr_sequencia	= nr_seq_autorizacao_w;

select 	/*+ INDEX(A MATPACI_I1) */	coalesce(sum(a.qt_material),0)
into STRICT	qt_utilizada_w
from	material_atend_paciente a
where	a.nr_atendimento		= nr_atendimento_w
and	a.cd_convenio		= cd_convenio_w
and	a.cd_material		= cd_material_w
and	coalesce(a.cd_motivo_exc_conta::text, '') = '';

return	qt_utilizada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_mat_util ( nr_seq_mat_autor_cirurgia_p bigint) FROM PUBLIC;

