CREATE OR REPLACE FUNCTION obter_qt_mg_carboplatina (nr_seq_paciente_p bigint) RETURNS bigint AS $body$
DECLARE


qt_mg_carboplatina_w		double precision;
ie_forma_w			varchar(10);
cd_pessoa_fisica_w		varchar(10);

BEGIN

/* pegar o valor da carboplatina */

select	max(a.cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	paciente_setor a
where	a.nr_seq_paciente	= nr_seq_paciente_p;

ie_forma_w	:= coalesce(Obter_Valor_Param_Usuario(281, 465, Obter_Perfil_Ativo, wheb_usuario_pck.get_nm_usuario, 0),'L');

if (ie_forma_w	= 'L') then
	qt_mg_carboplatina_w	:= (obter_dados_loco_regional(cd_pessoa_fisica_w,4))::numeric;
else
	select	max(qt_mg_carboplatina)
	into STRICT	qt_mg_carboplatina_w
	from	paciente_setor
	where	nr_seq_paciente	= nr_seq_paciente_p;
end if;


return	qt_mg_carboplatina_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
