SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_mg_carbo_ciclo (nr_seq_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_mg_carboplatina_w		double precision;
ie_forma_w			varchar(10);
cd_pessoa_fisica_w		varchar(10);
nr_seq_paciente_w		bigint;

BEGIN

/* pegar o valor da carboplatina */

select	a.cd_pessoa_fisica,
	a.nr_seq_paciente
into STRICT	cd_pessoa_fisica_w,
	nr_seq_paciente_w
from	paciente_setor a,
	paciente_atendimento b
where	a.nr_seq_paciente	= b.nr_seq_paciente
and	b.nr_seq_atendimento	= nr_seq_atendimento_p;

ie_forma_w	:= coalesce(Obter_Valor_Param_Usuario(281, 465, Obter_Perfil_Ativo, wheb_usuario_pck.get_nm_usuario, 0),'L');

if (ie_forma_w	= 'L') then
	qt_mg_carboplatina_w	:= (obter_dados_loco_regional(cd_pessoa_fisica_w,4))::numeric;
else
	select	max(qt_mg_carboplatina)
	into STRICT	qt_mg_carboplatina_w
	from	paciente_atendimento
	where	nr_seq_atendimento	= nr_seq_atendimento_p;

	if (coalesce(qt_mg_carboplatina_w::text, '') = '') then

		select	max(qt_mg_carboplatina)
		into STRICT	qt_mg_carboplatina_w
		from	paciente_setor
		where	nr_seq_paciente	= nr_seq_paciente_w;

	end if;
end if;


return	qt_mg_carboplatina_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_mg_carbo_ciclo (nr_seq_atendimento_p bigint) FROM PUBLIC;

