CREATE OR REPLACE FUNCTION obter_qt_min_max_local_estoque ( cd_local_estoque_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p
I - Minimo
A - Maximo*/
qt_retorno_w				integer;
qt_dia_ajuste_minimo_w			integer;
qt_dia_ajuste_maximo_w			integer;


BEGIN

select	qt_dia_ajuste_minimo,
	qt_dia_ajuste_maximo
into STRICT	qt_dia_ajuste_minimo_w,
	qt_dia_ajuste_maximo_w
from	local_Estoque
where	cd_local_estoque = cd_local_estoque_p;

if (ie_opcao_p = 'I') then
	qt_retorno_w := qt_dia_ajuste_minimo_w;
elsif (ie_opcao_p = 'A') then
	qt_retorno_w := qt_dia_ajuste_maximo_w;
end if;

return	qt_retorno_w;

end;
$body$
