SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_min_max_sinal_vital ( ds_coluna_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

ie_opcao_w	varchar(3);
qt_retorno_w	double precision;
nr_sequencia_w	smallint;

/*
	MIN - Mínimo
	MAX - Máximo
*/
BEGIN

if (ds_coluna_p = 'PA Sist') then
	nr_sequencia_w	:= 1;
elsif (ds_coluna_p = 'PA Diast') then
		nr_sequencia_w	:= 2;
elsif (ds_coluna_p = 'PAM') then
		nr_sequencia_w	:= 8;
elsif (ds_coluna_p = 'FC') then
		nr_sequencia_w	:= 9;
elsif (ds_coluna_p = 'FR') then
		nr_sequencia_w	:= 3;
elsif (ds_coluna_p = 'Temp') then
		nr_sequencia_w	:= 4;
elsif (ds_coluna_p = 'Peso') then
		nr_sequencia_w	:= 12;
elsif (ds_coluna_p = 'Altura') then
		nr_sequencia_w	:= 13;
elsif (ds_coluna_p = 'IMC') then
		nr_sequencia_w	:= 14;
elsif (ds_coluna_p = 'SC') then
		nr_sequencia_w	:= 15;
elsif (ds_coluna_p = 'Glic cap') then
		nr_sequencia_w	:= 	7;
elsif (ds_coluna_p = 'Sat') then
		nr_sequencia_w	:= 	6;
elsif (ds_coluna_p = 'PVC') then
		nr_sequencia_w	:= 	16;
elsif (ds_coluna_p = 'PAE') then
		nr_sequencia_w	:= 	18;
elsif (ds_coluna_p = 'ICP') then
		nr_sequencia_w	:= 	19;
elsif (ds_coluna_p = 'P int abd') then
		nr_sequencia_w	:= 	20;
elsif (ds_coluna_p = 'BCF') then
		nr_sequencia_w	:= 	11;
elsif (ds_coluna_p = 'Temp inc') then
		nr_sequencia_w	:= 	21;
end if;

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	if (ie_opcao_p	= 'MIN') then
		select	max(qt_minimo)
		into STRICT	qt_retorno_w
		from	sinal_vital
		where	nr_sequencia = nr_sequencia_w;
	elsif (ie_opcao_p = 'MAX') then
		select	max(vl_maximo)
		into STRICT	qt_retorno_w
		from	sinal_vital
		where	nr_sequencia = nr_sequencia_w;

		if (qt_retorno_w = 0) then
			qt_retorno_w	:= 100;
		end if;
	end if;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
