CREATE OR REPLACE FUNCTION obter_qt_movto_inventario_mat ( cd_estabelecimento_p bigint, cd_material_p bigint, ie_entrada_saida_p text, dt_mesano_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_movimento_w		double precision;
cd_material_w		bigint;


BEGIN

select	cd_material_estoque
into STRICT	cd_material_w
from	material
where	cd_material = cd_material_p;

select	sum(qt_estoque)
into STRICT	qt_movimento_w
from	movimento_estoque_v2 a,
	operacao_estoque b
where	a.cd_operacao_estoque	= b.cd_operacao_estoque
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.dt_mesano_referencia	= dt_mesano_referencia_p
and	b.ie_tipo_requisicao		= 5
and	b.ie_entrada_saida		= ie_entrada_saida_p
and	a.cd_material_estoque	= cd_material_w;

return	qt_movimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
