CREATE OR REPLACE FUNCTION obter_qt_movto_roupa (cd_barra_p text) RETURNS bigint AS $body$
DECLARE


qt_movimento_w		bigint;


BEGIN

select 	count(a.nr_sequencia)
into STRICT	qt_movimento_w
from	rop_roupa c,
		rop_lote_movto a,
		rop_movto_roupa b
where 	a.nr_sequencia = b.nr_seq_lote
and 	c.nr_sequencia = b.nr_seq_roupa
and 	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and 	c.cd_barra_roupa = cd_barra_p;

return	qt_movimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
