CREATE OR REPLACE FUNCTION obter_qt_necessita_exames (nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_necessita_exame_w	    varchar(25) := 'N';


BEGIN

	if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
		select coalesce(max('S'),'N')
		into STRICT ds_necessita_exame_w
		from paciente_atend_proc
		where nr_seq_atendimento = nr_seq_atendimento_p;
	end if;

	return substr(obter_desc_campo_grid(336203, substr(ds_necessita_exame_w,1,255)),1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
