SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_nr_seq_autor (nr_seq_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_autor_w bigint;
nr_seq_paciente_w paciente_atendimento.nr_seq_paciente%type;


BEGIN

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then

    select  max(nr_seq_paciente)
    into STRICT    nr_seq_paciente_w
    from    paciente_atendimento 
    where   nr_seq_atendimento = nr_seq_atendimento_p;

    select	max(a.nr_sequencia)
    into STRICT	nr_sequencia_autor_w
    from	autorizacao_convenio a
    where a.nr_seq_paciente_setor = nr_seq_paciente_w;
end if;

return nr_sequencia_autor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
