CREATE OR REPLACE FUNCTION obter_qt_nursing_interv (nr_seq_pe_procedimento_p bigint) RETURNS varchar AS $body$
DECLARE


exists_reg_w  varchar(1);


BEGIN

select coalesce(max('S'), 'N')
into STRICT exists_reg_w
from cp_intervention ci
where ci.nr_seq_pe_procedimento = nr_seq_pe_procedimento_p
and exists (SELECT 1
			from pe_procedimento pp
			where pp.nr_sequencia = ci.nr_seq_pe_procedimento);

return exists_reg_w;
			
end;
