CREATE OR REPLACE FUNCTION obter_qt_nut_n_proteicas (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_total_n_proteic_w	double precision;


BEGIN

-- Quantidade de elementos não protéicos
select	count(*)
into STRICT	qt_total_n_proteic_w
from	nut_pac_elemento a,
	nut_pac_elem_mat b,
	nut_elemento c
where	a.nr_seq_nut_pac	= nr_sequencia_p
and	a.nr_sequencia		= b.nr_seq_pac_elem
and	a.nr_seq_elemento	= c.nr_sequencia
and	c.ie_tipo_elemento	<> 'P';

return	qt_total_n_proteic_w;

end;
