SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_oci_trans_consist ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, ie_tipo_atend_p text, ie_tipo_consist_p text) RETURNS bigint AS $body$
DECLARE


qt_material_w			double precision;
cd_unidade_medida_compra_w	varchar(30);
cd_unidade_medida_estoque_w	varchar(30);
ie_unidade_medida_w		varchar(3);
cd_estabelecimento_w		smallint := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN
select	substr(obter_dados_material_estab(a.cd_material,cd_estabelecimento_w,'UME'),1,30) cd_unidade_medida_estoque,
	b.cd_unidade_medida_compra
into STRICT	cd_unidade_medida_estoque_w,
	cd_unidade_medida_compra_w
from	material a,
	ordem_compra_item b
where	a.cd_material = b.cd_material
and	b.nr_ordem_compra = nr_ordem_compra_p
and	b.nr_item_oci = nr_item_oci_p;

if (cd_unidade_medida_estoque_w = cd_unidade_medida_compra_w) then
	ie_unidade_medida_w	:= 'UME';
else
	ie_unidade_medida_w	:= 'UMP';
end if;

select	coalesce(sum(qt_material), 0)
into STRICT	qt_material_w
from	ordem_compra_item_cb
where	nr_ordem_compra		= nr_ordem_compra_p
and	nr_item_oci		= nr_item_oci_p
and	ie_atende_recebe	= ie_tipo_atend_p
and (ie_tipo_consist_p = 'X' or coalesce(ie_status,'CB') = ie_tipo_consist_p)
and	coalesce(nr_seq_nota::text, '') = '';

return	qt_material_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
