SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_oci_trans_nota ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, ie_entrada_saida_p text) RETURNS bigint AS $body$
DECLARE


qt_material_w		double precision;
ie_unidade_medida_w	varchar(3);
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN
select	CASE WHEN b.cd_unidade_medida_compra=substr(obter_dados_material_estab(a.cd_material,cd_estabelecimento_w,'UME'),1,30) THEN  'UME'  ELSE 'UMP' END
into STRICT	ie_unidade_medida_w
from	ordem_compra_item b,
	material a
where	a.cd_material = b.cd_material
and	b.nr_ordem_compra = nr_ordem_compra_p
and	b.nr_item_oci = nr_item_oci_p;

if (ie_entrada_saida_p = 'S') then
	select	coalesce(sum(a.qt_item_nf),0)
	into STRICT	qt_material_w
	from	nota_fiscal b,
		nota_fiscal_item a
	where	a.nr_ordem_compra = nr_ordem_compra_p
	and		a.nr_item_oci	= nr_item_oci_p
	and		b.nr_sequencia = a.nr_sequencia
	and		b.ie_situacao = '1'
	and		obter_se_nota_entrada_saida(a.nr_sequencia) = 'S';
else
	select	coalesce(sum(a.qt_item_nf),0)
	into STRICT	qt_material_w
	from	nota_fiscal b,
		nota_fiscal_item a
	where	a.nr_ordem_compra = nr_ordem_compra_p
	and		a.nr_item_oci	= nr_item_oci_p
	and		b.nr_sequencia = a.nr_sequencia
	and		b.ie_situacao = '1'
	and		obter_se_nota_entrada_saida(a.nr_sequencia) = 'E';
end if;

return	qt_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
