CREATE OR REPLACE FUNCTION obter_qt_ocup_agenda_per (cd_agenda_p bigint, dt_agenda_inicial_p timestamp, dt_agenda_final_p timestamp, ie_opcao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	double precision;
ie_tipo_ocup_w	varchar(1);
qt_hor_livre_w	bigint := 0;
qt_hor_agenda_w	bigint := 0;
qt_hor_ocup_w	bigint := 0;

/* Opção
	O - Ocupado
	L - Livre
*/
BEGIN

ie_tipo_ocup_w := obter_param_usuario(820, 300, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_tipo_ocup_w);

select	count(*)
into STRICT	qt_hor_agenda_w
from	agenda_paciente
where	cd_agenda		= cd_agenda_p
and	dt_agenda		between dt_agenda_inicial_p and fim_dia(dt_agenda_final_p)
and	ie_status_agenda not in ('C','B','II','F','I');

select	count(*)
into STRICT	qt_hor_livre_w
from	agenda_paciente
where	cd_agenda		= cd_agenda_p
and	dt_agenda		between dt_agenda_inicial_p and fim_dia(dt_agenda_final_p)
and	ie_status_agenda = 'L';

select	count(*)
into STRICT	qt_hor_ocup_w
from	agenda_paciente
where	cd_agenda		= cd_agenda_p
and	dt_agenda		between dt_agenda_inicial_p and fim_dia(dt_agenda_final_p)
and	ie_status_agenda not in ('L','C','B','II','F','I');

if (ie_opcao_p = 'O') then
	if (ie_tipo_ocup_w = 'P') then
		select (dividir(qt_hor_ocup_w,qt_hor_agenda_w) * 100)
		into STRICT	vl_retorno_w
		;
	else
		vl_retorno_w := qt_hor_ocup_w;
	end if;
elsif (ie_opcao_p = 'L') then
	if (ie_tipo_ocup_w = 'P') then
		select (dividir(qt_hor_livre_w,qt_hor_agenda_w) * 100)
		into STRICT	vl_retorno_w
		;
	else
		vl_retorno_w := qt_hor_livre_w;
	end if;
end if;


return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
