SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_os_grupo_atual ( nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


/* Utilizada para os calculos das cores de metas*/

qt_ordem_w		integer;
nr_seq_grupo_w		bigint;
cd_perfil_ativo_w		integer := obter_perfil_ativo;



BEGIN

nr_seq_grupo_w	:= obter_grupo_usuario_wheb(nm_usuario_p);

select	count(*)
into STRICT	qt_ordem_w
from	man_ordem_servico a
where (a.nr_grupo_trabalho in (
	SELECT	a.nr_sequencia
        from	man_grupo_trabalho a,
		man_grupo_trab_usuario b
        where	a.nr_sequencia = b.nr_seq_grupo_trab
	and	b.nm_usuario_param in (
		SELECT	nm_usuario_grupo
		from	usuario_grupo_des
		where	nr_seq_grupo = nr_seq_grupo_w)))
and	coalesce(a.nr_seq_meta_pe::text, '') = ''
and	coalesce(a.nr_seq_obj_bsc::text, '') = ''
and	a.nr_seq_estagio in (
	select	nr_seq_estagio
	from	man_estagio_usuario
	where	nm_usuario_acao in (
		select	nm_usuario_grupo
		from	usuario_grupo_des
		where	nr_seq_grupo = nr_seq_grupo_w)
	and	CASE WHEN coalesce(cd_perfil,0)=0 THEN coalesce(cd_perfil_ativo_w,0)  ELSE cd_perfil END  =
		CASE WHEN coalesce(cd_perfil_ativo_w,0)=0 THEN CASE WHEN coalesce(cd_perfil,0)=0 THEN coalesce(cd_perfil_ativo_w,0)  ELSE cd_perfil END   ELSE cd_perfil_ativo_w END )
and	a.ie_status_ordem in ('1','2')
and	a.nr_seq_grupo_des	= nr_seq_grupo_w;

return qt_ordem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_os_grupo_atual ( nm_usuario_p text) FROM PUBLIC;

