CREATE OR REPLACE FUNCTION obter_qt_os_painel_sup ( nr_seq_gerencia_sup_p bigint, nr_seq_grupo_sup_p bigint, ie_informacao_p text ) RETURNS bigint AS $body$
DECLARE


qt_retorno_w			bigint;
qt_total_os_w			bigint;
qt_tot_os_antiga_prev_w		bigint;
qt_tot_os_antiga_w		bigint;


BEGIN

select	sum(qt_ordem) qt_ordem,
	sum(qt_tot_os_antiga) qt_tot_os_antiga,
	sum(qt_tot_prev_os_antiga) qt_tot_prev_os_antiga
into STRICT	qt_total_os_w,
	qt_tot_os_antiga_w,
	qt_tot_os_antiga_prev_w
from	(	SELECT	0 ie_tipo,
			b.ds_grupo ds_grupo_sup,
			a.nr_seq_grupo_sup,
			count(*) qt_ordem,
			coalesce(sum(CASE WHEN obter_se_data_periodo(trunc(a.dt_ordem_servico, 'dd'), to_date('01/01/2007','dd/mm/yyyy'), trunc(clock_timestamp() - interval '15 days','dd'))='S' THEN  1  ELSE 0 END ),0) qt_tot_os_antiga,
			coalesce(sum(CASE WHEN obter_se_os_antiga(a.nr_sequencia)=1 THEN CASE WHEN man_obter_data_prevista_os_sup(a.nr_sequencia)=trunc(clock_timestamp()) THEN 1  ELSE 0 END  END ),0) qt_tot_prev_os_antiga
		from	man_ordem_servico a,
			grupo_suporte b,
			gerencia_wheb c
		where	a.nr_seq_grupo_sup = b.nr_sequencia
		and	b.nr_seq_gerencia_sup = c.nr_sequencia
		and	obter_se_os_suporte(a.nr_sequencia) = 'S'
		and	a.ie_status_ordem <> 3
		and	c.nr_sequencia = nr_seq_gerencia_sup_p
		and	((a.nr_seq_grupo_sup = nr_seq_grupo_sup_p) or (nr_seq_grupo_sup_p = 0))
		group by b.ds_grupo, a.nr_seq_grupo_sup
	) alias21;

if (ie_informacao_p = 1) then
	qt_retorno_w := coalesce(qt_total_os_w,0);
elsif (ie_informacao_p = 2) then
	qt_retorno_w := coalesce(qt_tot_os_antiga_w,0);
elsif (ie_informacao_p = 3) then
	qt_retorno_w := coalesce(qt_tot_os_antiga_prev_w,0);
end if;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
