CREATE OR REPLACE FUNCTION obter_qt_os_prev (nr_seq_ordem_serv_p bigint, nm_usuario_exec_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


dt_prevista_w	timestamp;
qt_resultado_w	bigint;

/*
ie_opcao_p
	'A' - Atrasada
	'S' - Sem previsão
	'H' - Previsão para hoje
	'P' - Prevista futuramente
*/
BEGIN

select	max(x.dt_prevista)
into STRICT	dt_prevista_w
from	man_ordem_ativ_prev x
where	x.nr_seq_ordem_serv	= nr_seq_ordem_serv_p
and	x.nm_usuario_prev	= nm_usuario_exec_p;

qt_resultado_w	:= 0;
if (dt_prevista_w IS NOT NULL AND dt_prevista_w::text <> '') then
	if (ie_opcao_p = 'A') and (dt_prevista_w < trunc(clock_timestamp(), 'dd')) then
		qt_resultado_w	:= 1;
	elsif (ie_opcao_p = 'H') and (dt_prevista_w = trunc(clock_timestamp(), 'dd')) then
		qt_resultado_w	:= 1;
	elsif (ie_opcao_p = 'P') and (dt_prevista_w > trunc(clock_timestamp(), 'dd')) then
		qt_resultado_w	:= 1;
	end if;
elsif (ie_opcao_p = 'S') and (coalesce(dt_prevista_w::text, '') = '') then
	qt_resultado_w	:= 1;
end if;

return	qt_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
