CREATE OR REPLACE FUNCTION obter_qt_os_usuario_atual ( nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


/* Utilizada para os calculos das cores de metas*/

qt_ordem_w		integer;
nr_seq_grupo_w		bigint;
cd_perfil_ativo_w	integer := obter_perfil_ativo;


BEGIN

nr_seq_grupo_w	:= obter_grupo_usuario_wheb(nm_usuario_p);

select 	count(*)
into STRICT	qt_ordem_w
from 	man_ordem_servico a
where 	coalesce(a.nr_seq_meta_pe::text, '') = ''
and 	coalesce(a.nr_seq_obj_bsc::text, '') = ''
and exists (
	SELECT 	1
	from 	man_estagio_usuario z
	where 	z.nm_usuario_acao = nm_usuario_p
	and 	z.nr_seq_estagio = a.nr_seq_estagio
	and	CASE WHEN coalesce(z.cd_perfil,0)=0 THEN coalesce(cd_perfil_ativo_w,0)  ELSE z.cd_perfil END  =
		CASE WHEN coalesce(cd_perfil_ativo_w,0)=0 THEN CASE WHEN coalesce(z.cd_perfil,0)=0 THEN coalesce(cd_perfil_ativo_w,0)  ELSE z.cd_perfil END   ELSE cd_perfil_ativo_w END )
and 	a.ie_status_ordem in ('1','2')
and 	substr(obter_nome_usuario_exec(a.nr_sequencia),1,60) = nm_usuario_p
and a.nr_seq_grupo_des   = nr_seq_grupo_w;

return qt_ordem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_os_usuario_atual ( nm_usuario_p text) FROM PUBLIC;

