CREATE OR REPLACE FUNCTION obter_qt_os_usuario_inicio_dia ( nm_usuario_p text, nr_seq_grupo_ref_p bigint) RETURNS bigint AS $body$
DECLARE


/* Utilizada para os calculos das cores de metas*/

qt_ordem_inicio_dia_w	integer;
nr_seq_grupo_w		bigint;


BEGIN

if (nr_seq_grupo_ref_p > 0) then
	nr_seq_grupo_w		:= nr_seq_grupo_ref_p;
else
	nr_seq_grupo_w		:= obter_grupo_usuario_wheb(nm_usuario_p);
end if;

select	coalesce(max(qt_ordem_servico), 0)
into STRICT	qt_ordem_inicio_dia_w
from	man_regra_resumo_meta
where	dt_referencia = Obter_inicio_semana(trunc(clock_timestamp(), 'dd'))
and	nm_usuario_exec = nm_usuario_p
and	nr_seq_grupo_des = nr_seq_grupo_w;


return qt_ordem_inicio_dia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
