CREATE OR REPLACE FUNCTION obter_qt_paciente_fila (nr_seq_pac_fila_p bigint) RETURNS bigint AS $body$
DECLARE


qt_paciente_w	bigint;


BEGIN

select  count(*)
into STRICT    qt_paciente_w
from    paciente_senha_fila
where   ((dt_fim_atendimento IS NOT NULL AND dt_fim_atendimento::text <> '') or (ie_rechamada  = 's') or (coalesce(dt_inicio_atendimento::text, '') = ''))
