CREATE OR REPLACE FUNCTION obter_qt_pacote_orig_pragma_atx (nr_sequencia_p bigint, nr_atendimento_p bigint, ie_pacote_p text) RETURNS bigint AS $body$
DECLARE


qt_pacote_orig_w	bigint	:= 0;
qt_pacote_w		bigint	:= 0;
qt_pacote_total_w	bigint	:= 0;
BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then

	if (coalesce(ie_pacote_p,'O') in ('O','T')) then
		select	count(*)
		into STRICT	qt_pacote_orig_w
		from	atendimento_pacote
		where	nr_seq_proc_origem = nr_sequencia_p
		and	nr_atendimento = nr_atendimento_p;
	end if;

	if (coalesce(ie_pacote_p,'O') in ('P','T')) then
		select	count(*)
		into STRICT	qt_pacote_w
		from	atendimento_pacote
		where	nr_seq_procedimento = nr_sequencia_p
		and	nr_atendimento = nr_atendimento_p;
	end if;

	qt_pacote_total_w	:= qt_pacote_orig_w + qt_pacote_w;

end if;


return	qt_pacote_total_w;

end;
$body$
LANGUAGE PLPGSQL
