CREATE OR REPLACE FUNCTION obter_qt_parcela_cartao (nr_seq_movto_parcela_p bigint) RETURNS bigint AS $body$
DECLARE


qt_parcela_w			integer;
nr_seq_movto_w			bigint;
dt_parcela_w			timestamp;


BEGIN
select	nr_seq_movto,
	dt_parcela
into STRICT	nr_seq_movto_w,
	dt_parcela_w
from	movto_cartao_cr_parcela
where	nr_sequencia	= nr_seq_movto_parcela_p;

select	count(*)
into STRICT	qt_parcela_w
from	movto_cartao_cr_parcela
where	nr_seq_movto	= nr_seq_movto_w
and	dt_parcela	<= dt_parcela_w;

return	qt_parcela_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
