CREATE OR REPLACE FUNCTION obter_qt_passagem_setor ( cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_retorno_p text) RETURNS bigint AS $body$
DECLARE

 
dt_inicial_w		timestamp;
dt_final_w		timestamp;
qt_passagem_setor_w	bigint;
			

BEGIN 
 
if (ie_retorno_p = 'M') then 
	dt_inicial_w	:= trunc(dt_inicial_p,'mm');
	dt_final_w	:= fim_mes(dt_final_p);
elsif (ie_retorno_p = 'P') then 
	dt_inicial_w	:= dt_inicial_p;
	dt_final_w	:= dt_final_p;
elsif (ie_retorno_p = 'P') then 
	dt_inicial_w	:= trunc(coalesce(dt_inicial_p, clock_timestamp()),'dd');
	dt_final_w	:= fim_dia(coalesce(dt_final_p, clock_timestamp()));
end if;
 
if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then 
 
	select	count(*) 
	into STRICT	qt_passagem_setor_w 
	from	setor_atendimento c, 
		atendimento_paciente b, 
		atend_paciente_unidade a 
	where	a.nr_atendimento	= b.nr_atendimento 
	and	a.cd_setor_atendimento	= c.cd_setor_atendimento 
	and	a.dt_entrada_unidade between dt_inicial_w and dt_final_w 
	and	c.cd_setor_atendimento	= cd_setor_atendimento_p;
 
end if;
 
 
return	qt_passagem_setor_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
