CREATE OR REPLACE FUNCTION obter_qt_pendencia_rep ( nr_atendimento_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w bigint;

BEGIN

select	count(*)
into STRICT		qt_retorno_w
from		pendencia_assinatura_rep_v
where		cd_perfil = cd_perfil_p
and			nm_usuario = nm_usuario_p;

return coalesce(qt_retorno_w,0);

end;
