CREATE OR REPLACE FUNCTION obter_qt_pend_entrega_oci ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nr_item_entrega_p bigint ) RETURNS bigint AS $body$
DECLARE

			
qt_pendente_w   ordem_compra_item_entrega.qt_prevista_entrega%type;			


BEGIN

begin
select  coalesce(b.qt_prevista_entrega,0) - coalesce(b.qt_real_entrega,0)
into STRICT    qt_pendente_w
from    ordem_compra_item_entrega b,
        ordem_compra_item a
where   a.nr_ordem_compra = nr_ordem_compra_p
and     a.nr_item_oci = nr_item_oci_p
and     a.nr_ordem_compra = b.nr_ordem_compra
and     a.nr_item_oci = b.nr_item_oci
and     b.nr_sequencia = nr_item_entrega_p
and     coalesce(b.dt_cancelamento::text, '') = '';
exception when no_data_found or too_many_rows then qt_pendente_w := 0;
end;

if (qt_pendente_w < 0) then
    qt_pendente_w := 0;
end if;

return qt_pendente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
