SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_pend_mat_oc ( cd_estabelecimento_p bigint, cd_material_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

 
/*ie_opcao_p 
A- Pendentes com entrega atrasadas 
M- Pendente para entregar no mÃªs vigente 
F- Pendentes para entrega futura (Superior a data atual) 
*/
			 
 
qt_material_w			double precision;
			

BEGIN 
 
if (ie_opcao_p = 'A') then 
 
	select	coalesce(sum(c.qt_prevista_entrega - coalesce(c.qt_real_entrega,0)),0) 
	into STRICT	qt_material_w 
	from	ordem_compra a, 
		ordem_compra_item b, 
		ordem_compra_item_entrega c 
	where	a.nr_ordem_compra = b.nr_ordem_compra 
	and	b.nr_ordem_compra = c.nr_ordem_compra 
	and	b.nr_item_oci = c.nr_item_oci 
	and	coalesce(a.dt_baixa::text, '') = '' 
	and	coalesce(a.nr_seq_motivo_cancel::text, '') = '' 
	and	coalesce(c.dt_cancelamento::text, '') = '' 
	and	a.cd_estabelecimento = cd_estabelecimento_p 
	and	b.cd_material = cd_material_p 
	and	trunc(c.dt_prevista_entrega,'dd') < trunc(clock_timestamp(),'dd');
 
elsif (ie_opcao_p = 'M') then 
 
	select	coalesce(sum(c.qt_prevista_entrega - coalesce(c.qt_real_entrega,0)),0) 
	into STRICT	qt_material_w 
	from	ordem_compra a, 
		ordem_compra_item b, 
		ordem_compra_item_entrega c 
	where	a.nr_ordem_compra = b.nr_ordem_compra 
	and	b.nr_ordem_compra = c.nr_ordem_compra 
	and	b.nr_item_oci = c.nr_item_oci 
	and	coalesce(a.dt_baixa::text, '') = '' 
	and	coalesce(a.nr_seq_motivo_cancel::text, '') = '' 
	and	coalesce(c.dt_cancelamento::text, '') = '' 
	and	a.cd_estabelecimento = cd_estabelecimento_p 
	and	b.cd_material = cd_material_p 
	and	trunc(c.dt_prevista_entrega,'MM') < trunc(clock_timestamp(),'MM');
	 
elsif (ie_opcao_p = 'F') then 
 
	select	coalesce(sum(c.qt_prevista_entrega - coalesce(c.qt_real_entrega,0)),0) 
	into STRICT	qt_material_w 
	from	ordem_compra a, 
		ordem_compra_item b, 
		ordem_compra_item_entrega c 
	where	a.nr_ordem_compra = b.nr_ordem_compra 
	and	b.nr_ordem_compra = c.nr_ordem_compra 
	and	b.nr_item_oci = c.nr_item_oci 
	and	coalesce(a.dt_baixa::text, '') = '' 
	and	coalesce(a.nr_seq_motivo_cancel::text, '') = '' 
	and	coalesce(c.dt_cancelamento::text, '') = '' 
	and	a.cd_estabelecimento = cd_estabelecimento_p 
	and	b.cd_material = cd_material_p 
	and	trunc(c.dt_prevista_entrega,'dd') > trunc(clock_timestamp(),'dd');
end if;
 
return	qt_material_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
