CREATE OR REPLACE FUNCTION obter_qt_pend_paciente (nr_seq_reserva_p bigint) RETURNS bigint AS $body$
DECLARE


qt_pendencia_pac_w	smallint;


BEGIN
if (nr_seq_reserva_p IS NOT NULL AND nr_seq_reserva_p::text <> '') then
	select 	count(1)
	into STRICT 	qt_pendencia_pac_w
	from 	san_reserva_item
	where 	nr_seq_reserva = nr_seq_reserva_p
	and		ie_pend_paciente = 'S';
end if;

return qt_pendencia_pac_w;

end;
$body$
