CREATE OR REPLACE FUNCTION obter_qt_permitida_kit_local ( cd_kit_material_p bigint, cd_local_estoque_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_kit_material_w	bigint;
qt_kit_local_w		bigint;


BEGIN

select	max(qt_kit_material)
into STRICT	qt_kit_material_w
from	regra_kit_local
where	cd_kit_material = cd_kit_material_p
and		cd_local_estoque = cd_local_estoque_p
and		cd_estabelecimento = cd_estabelecimento_p;

select	count(*)
into STRICT	qt_kit_local_w
from	kit_estoque
where	coalesce(nr_seq_reg_kit::text, '') = ''
and 	coalesce(dt_utilizacao::text, '') = ''
and		cd_kit_material = cd_kit_material_p
and		cd_local_estoque = cd_local_estoque_p
and		cd_estabelecimento = cd_estabelecimento_p;

return	qt_kit_material_w - qt_kit_local_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
