CREATE OR REPLACE FUNCTION obter_qt_peso_sv_data (nr_atendimento_p bigint, dt_parametro_p timestamp, dt_parametro_fim_p timestamp, cd_setor_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

qt_peso_w	bigint;
nr_sequencia_w	bigint;


BEGIN

select  coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	atendimento_sinal_vital
where	ie_situacao = 'A'
and		nr_atendimento = nr_atendimento_p
and		coalesce(qt_peso,0) > 0
and		trunc(dt_liberacao)   < trunc(dt_parametro_fim_p)
and		trunc(dt_liberacao) 	>= trunc(dt_parametro_p)
and		coalesce(cd_setor_atendimento, cd_setor_atendimento_p) = cd_setor_atendimento_p;

if (nr_sequencia_w > 0)	then
	select	max(qt_peso)
	into STRICT	qt_peso_w
	from	atendimento_sinal_vital
	where	nr_sequencia = nr_sequencia_w;
end if;


return	qt_peso_w;

end;
