CREATE OR REPLACE FUNCTION obter_qt_prescr_lib_hoje_vig (nr_atendimento_p bigint, ie_origem_inf_p text default '0') RETURNS bigint AS $body$
DECLARE

 
qt_retorno_w		integer;
dt_fim_dia_w		timestamp;
dt_inicio_w		timestamp	:= trunc(clock_timestamp());
cd_setor_atendimento_w	integer;
qt_hora_vigencia_w		timestamp;


BEGIN 
 
dt_fim_dia_w	:= trunc(clock_timestamp(),'dd') + 86399/86400;
 
Select max(b.cd_setor_atendimento) 
into STRICT	cd_setor_atendimento_w 
from	atendimento_paciente a, 
		unidade_atendimento b 
where 	b.nr_atendimento    = a.nr_atendimento 
and		a.nr_atendimento 		= nr_atendimento_p;
 
if (coalesce(cd_setor_atendimento_w,0) > 0) then 
 
	select max(hr_inicio_prescricao) 
	into STRICT	qt_hora_vigencia_w 
	from	setor_atendimento 
	where	cd_setor_atendimento = cd_setor_atendimento_w;
 
	dt_fim_dia_w	:= to_date(to_char((clock_timestamp() + interval '1 days'),'dd/mm/yyyy')||' ' ||(to_char(( qt_hora_vigencia_w + 86399/86400),'hh24:mi:ss')),'dd/mm/yyyy hh24:mi:ss');
 
	Select	count(*) 
	into STRICT	qt_retorno_w 
	from	prescr_medica 
	where	nr_atendimento	= nr_atendimento_p 
	and ((dt_liberacao_medico IS NOT NULL AND dt_liberacao_medico::text <> '') or (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')) 
	and	dt_prescricao between dt_inicio_w and dt_fim_dia_w 
	and	((ie_origem_inf_p	= '0') or (ie_origem_inf = ie_origem_inf_p));
 
 
else 
 
	Select	count(*) 
	into STRICT	qt_retorno_w 
	from	prescr_medica 
	where	nr_atendimento	= nr_atendimento_p 
	and ((dt_liberacao_medico IS NOT NULL AND dt_liberacao_medico::text <> '') or (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')) 
	and	dt_prescricao between dt_inicio_w and dt_fim_dia_w 
	and	((ie_origem_inf_p	= '0') or (ie_origem_inf = ie_origem_inf_p));
	 
end if;
 
return qt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
