CREATE OR REPLACE FUNCTION obter_qt_prescr_pendente (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_proc_w	bigint;


BEGIN

select	count(b.cd_procedimento)
into STRICT	qt_proc_w
from	prescr_procedimento b,
	prescr_medica a
where	a.nr_prescricao		= b.nr_prescricao
and	coalesce(b.nr_seq_origem::text, '') = ''
and	a.nr_atendimento	= nr_atendimento_p
and	(coalesce(a.dt_liberacao, a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao, a.dt_liberacao_medico))::text <> '')
and	b.cd_motivo_baixa	= 0
and	b.ie_suspenso		= 'N';

return	qt_proc_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_prescr_pendente (nr_atendimento_p bigint) FROM PUBLIC;

