SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_prescr_procedimento (nr_prescricao_p bigint, ie_restrige_p text) RETURNS varchar AS $body$
DECLARE


qt_w	bigint  := 0;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (ie_restrige_p <> 'S') then

	select 	count(*)
	into STRICT	qt_w
	from 	prescr_procedimento b
	where 	b.nr_prescricao = nr_prescricao_p;

elsif (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (ie_restrige_p = 'S') then

	select 	count(*)
	into STRICT	qt_w
	from 	prescr_procedimento b
	where 	b.nr_prescricao = nr_prescricao_p
	and	coalesce(b.ie_via_acesso::text, '') = '';

end if;

return qt_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
