CREATE OR REPLACE FUNCTION obter_qt_prev_oci_inspecao ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_prevista_entrega_w		double precision;
nr_seq_entrega_w			bigint;
nr_ordem_compra_w		bigint;
nr_item_oci_w			integer;

c01 CURSOR FOR
SELECT	coalesce(nr_seq_entrega,0),
	nr_ordem_compra,
	nr_item_oci
from	inspecao_recebimento
where	nr_sequencia = nr_sequencia_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_entrega_w,
	nr_ordem_compra_w,
	nr_item_oci_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	nr_seq_entrega_w	:= nr_seq_entrega_w;
	nr_ordem_compra_w	:= nr_ordem_compra_w;
	nr_item_oci_w		:= nr_item_oci_w;

	end;
end loop;
close C01;

if (nr_seq_entrega_w > 0) then
	select	coalesce(qt_prevista_entrega,0)
	into STRICT	qt_prevista_entrega_w
	from	ordem_compra_item_entrega
	where	nr_sequencia = nr_seq_entrega_w;
else
	select	coalesce(obter_quantidade_oci(nr_ordem_compra_w,nr_item_oci_w),0)
	into STRICT	qt_prevista_entrega_w
	;
end if;

return	qt_prevista_entrega_w;

end;
$body$
