SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_proced_prescricao (nr_seq_pepo_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		procedimento_paciente.nr_sequencia%type := 0;
nr_sequencia_ww	procedimento_paciente.nr_sequencia%type := 0;

c01 CURSOR FOR
	SELECT 	nr_sequencia
	from 		procedimento_paciente a,
				cirurgia b,
				procedimento c
	where   	a.nr_cirurgia		= b.nr_cirurgia
	and		a.cd_procedimento 	= b.cd_procedimento_princ
	and		a.ie_origem_proced 	= b.ie_origem_proced
	and		a.cd_procedimento 	= c.cd_procedimento
	and		a.ie_origem_proced 	= c.ie_origem_proced
	and		c.ie_classificacao 		= 1
	and		b.ie_status_cirurgia 		not in (3,4)
	and		b.nr_seq_pepo 		= nr_seq_pepo_p
	and	not exists (	SELECT	1
							from		procedimento_autorizado x,
										autorizacao_convenio y,
										agenda_paciente k
			where						x.nr_sequencia_autor 	= y.nr_sequencia
			and						k.nr_sequencia       	= y.nr_seq_agenda
			and						k.nr_cirurgia	 	= a.nr_cirurgia
			and						x.cd_procedimento 	= a.cd_procedimento
			and						x.ie_origem_proced 	= a.ie_origem_proced
			and						(x.nr_seq_autor_pacote IS NOT NULL AND x.nr_seq_autor_pacote::text <> ''));


BEGIN
if (coalesce(nr_seq_pepo_p,0) > 0) then
	open C01;
	loop
	fetch C01 into
		nr_sequencia_ww;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (nr_sequencia_w > 0) then
			nr_sequencia_w := 0;
		else
			nr_sequencia_w := nr_sequencia_ww;
		end if;
		end;
	end loop;
	close C01;
end if;

return	nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_proced_prescricao (nr_seq_pepo_p bigint) FROM PUBLIC;

