CREATE OR REPLACE FUNCTION obter_qt_proced_sus ( nr_atendimento_p bigint, qt_max_proced_p bigint, cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS bigint AS $body$
DECLARE


qt_proced_w		bigint;
qt_material_w		double precision;
qt_procedimento_w	double precision;


BEGIN

Select	coalesce(sum(qt_material),0)
into STRICT	qt_material_w
from	material_atend_paciente
where	nr_atendimento	= nr_atendimento_p
and	cd_material	= cd_material_p;


select	coalesce(sum(qt_procedimento),0)
into STRICT	qt_procedimento_w
from	procedimento_paciente
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p
and	nr_atendimento		= nr_atendimento_p
and	coalesce(cd_motivo_exc_conta::text, '') = '';

if (qt_material_w > qt_max_proced_p) then
	qt_material_w	:= qt_max_proced_p;
end if;


qt_proced_w	:= qt_material_w - qt_procedimento_w;

if (qt_proced_w < 0) then
	qt_proced_w := 0;
end if;

RETURN qt_proced_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
