CREATE OR REPLACE FUNCTION obter_qt_proc_conta_sus ( nr_interno_conta_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, dt_competencia_p timestamp) RETURNS bigint AS $body$
DECLARE

			
qt_retorno_w		integer	:= 0;
qt_proc_dia_w		integer	:= 0;


BEGIN

qt_proc_dia_w	:= coalesce(sus_validar_regra(28,cd_procedimento_p,ie_origem_proced_p,dt_competencia_p),0);

if (qt_proc_dia_w = 0) then
	begin
	select	sum(coalesce(qt_procedimento,0))
	into STRICT	qt_retorno_w
	from	procedimento_paciente
	where	nr_interno_conta	= nr_interno_conta_p
	and	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p
	and	trunc(dt_procedimento, 'mm') = trunc(dt_competencia_p, 'mm')
	and	coalesce(cd_motivo_exc_conta::text, '') = '';
	end;
else
	begin	
	select	sum(coalesce(qt_procedimento,0))
	into STRICT	qt_retorno_w
	from	procedimento_paciente
	where	nr_interno_conta	= nr_interno_conta_p
	and	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p
	and	coalesce(cd_motivo_exc_conta::text, '') = '';
	end;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
