CREATE OR REPLACE FUNCTION obter_qt_proc_pendente_prescr ( cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint, cd_perfil_p bigint, nr_atendimento_p bigint, qt_minutos_p bigint ) RETURNS bigint AS $body$
DECLARE

qt_hor_pendente_w	 bigint := 0;

BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	 
	select	count(*) 
	into STRICT	qt_hor_pendente_w 
	from 	adep_pend_prev_v 
	where	dt_horario		< clock_timestamp()		 
	and	nr_atendimento		= nr_atendimento_p 
	and	ie_tipo_item		in ('P','L') 
	and	ADEP_obter_se_consiste_item(ie_tipo_item, cd_perfil_p, CASE WHEN ie_tipo_item='P' THEN cd_item  ELSE null END ,CASE WHEN ie_tipo_item='P' THEN ie_origem_proced  ELSE null END , nr_seq_proc_interno) = 'S';		
	 
end if;
 
return	qt_hor_pendente_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
