CREATE OR REPLACE FUNCTION obter_qt_registro_material ( nm_tabela_p text, cd_material_p integer) RETURNS bigint AS $body$
DECLARE


qt_registro_w		double precision;
ds_comando_w		varchar(32000) := '';


BEGIN


ds_comando_w	:=	'select count(*) from ' || nm_tabela_p ||
			' where cd_material = ' || cd_material_p;

qt_registro_w := Obter_Valor_dinamico(ds_comando_w, qt_registro_w);


