CREATE OR REPLACE FUNCTION obter_qt_registro_ptu_a400 ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_tot_r402_w			integer;
qt_tot_r403_w			integer;
qt_tot_r404_w			integer;
qt_tot_r405_w			integer;
qt_tot_r406_w			integer;
qt_tot_r407_w			integer;
qt_tot_r408_w			integer;
qt_tot_r410_w			integer;
qt_tot_r452_w			integer;
qt_total_w			integer;


BEGIN
if (ie_opcao_p = '402') then
	select	count(1)
	into STRICT	qt_tot_r402_w
	from	ptu_prestador		a
	where	a.nr_seq_movimento	= nr_sequencia_p;

	qt_total_w	:= qt_tot_r402_w;
elsif (ie_opcao_p = '403') then
	select	count(1)
	into STRICT	qt_tot_r403_w
	from	ptu_prestador_endereco	b,
		ptu_prestador		a
	where	b.nr_seq_prestador	= a.nr_sequencia
	and	a.nr_seq_movimento	= nr_sequencia_p;

	qt_total_w	:= qt_tot_r403_w;
elsif (ie_opcao_p = '404') then
	select	count(1)
	into STRICT	qt_tot_r404_w
	from	ptu_prestador_grupo_serv	b,
		ptu_prestador_endereco		c,
		ptu_prestador			a
	where	b.nr_seq_endereco	= c.nr_sequencia
	and	c.nr_seq_prestador	= a.nr_sequencia
	and	a.nr_seq_movimento	= nr_sequencia_p;

	qt_total_w	:= qt_tot_r404_w;
elsif (ie_opcao_p = '405') then
	select	count(1)
	into STRICT	qt_tot_r405_w
	from	ptu_prestador_rede_ref	b,
		ptu_prestador		a
	where	b.nr_seq_prestador	= a.nr_sequencia
	and	a.nr_seq_movimento	= nr_sequencia_p;

	qt_total_w	:= qt_tot_r405_w;

elsif (ie_opcao_p = '406') then
	select	count(1)
	into STRICT	qt_tot_r406_w
	from	ptu_prestador_excl_subst	b,
		ptu_prestador			a
	where	b.nr_seq_prestador	= a.nr_sequencia
	and	a.nr_seq_movimento	= nr_sequencia_p;

	qt_total_w	:= qt_tot_r406_w;

elsif (ie_opcao_p = '407') then
	select	count(1)
	into STRICT	qt_tot_r407_w
	from	ptu_prestador_imposto		b,
		ptu_prestador			a
	where	b.nr_seq_prestador	= a.nr_sequencia
	and	a.nr_seq_movimento	= nr_sequencia_p;

	qt_total_w	:= qt_tot_r407_w;

elsif (ie_opcao_p = '408') then
	select	count(1)
	into STRICT	qt_tot_r408_w
	from	ptu_prestador_qualific		b,
		ptu_prestador			a
	where	b.nr_seq_prestador	= a.nr_sequencia
	and	a.nr_seq_movimento	= nr_sequencia_p;

	qt_total_w	:= qt_tot_r408_w;

elsif (ie_opcao_p = '410') then
	select	count(1)
	into STRICT	qt_tot_r410_w
	from	ptu_prestador_observacao	b,
		ptu_prestador			a
	where	b.nr_seq_prestador	= a.nr_sequencia
	and	a.nr_seq_movimento	= nr_sequencia_p;

	qt_total_w	:= qt_tot_r410_w;

elsif (ie_opcao_p = '452') then
	select	sum(qt_reg)
	into STRICT	qt_tot_r452_w
	from (SELECT	count(1) qt_reg
		from	ptu_rede_ref_produto	e,
			ptu_rede_referenciada	d,
			ptu_prestador_rede_ref	c,
			ptu_prestador		b,
			ptu_movimento_prestador	a
		where	a.nr_sequencia		= b.nr_seq_movimento
		and	c.nr_seq_prestador	= b.nr_sequencia
		and	d.cd_rede		= c.cd_rede
		and	e.cd_rede		= d.cd_rede
		and	a.nr_sequencia		= nr_sequencia_p
		and	not exists (select	1
					from	ptu_movimento_rede_ref	x
					where	x.nr_seq_movimento	= a.nr_sequencia)
		
union all

		SELECT	count(1) qt_reg
		from	ptu_movimento_rede_ref	b,
			ptu_movimento_prestador	a
		where	b.nr_seq_movimento	= a.nr_sequencia
		and	a.nr_sequencia		= nr_sequencia_p) alias5;

	qt_total_w	:= qt_tot_r452_w;
end if;

return	qt_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
