CREATE OR REPLACE FUNCTION obter_qt_registro_qd (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE
	-- Change this to reflect the dblink connection string
	v_conn_str  text := format('port=%s dbname=%s user=%s', current_setting('port'), current_database(), current_user);
	v_query     text;

	v_ret	bigint;
BEGIN
	v_query := 'SELECT * FROM obter_qt_registro_qd_atx ( ' || quote_nullable(nr_atendimento_p) || ' )';
	SELECT * INTO v_ret FROM dblink(v_conn_str, v_query) AS p (ret bigint);
	RETURN v_ret;

END;
$body$ LANGUAGE plpgsql SECURITY DEFINER;




CREATE OR REPLACE FUNCTION obter_qt_registro_qd_atx (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_reg_w		bigint;
cd_risco_queda_w	bigint;
BEGIN

select 	coalesce(max(cd_risco_queda),0)
into STRICT	cd_risco_queda_w
from	parametro_atendimento;


select	count(*)
into STRICT	qt_reg_w
from	PE_PRESCR_DIAG a,
	PE_PRESCRICAO b
where	a.nr_seq_diag	= cd_risco_queda_w
and 	a.nr_seq_prescr = b.nr_sequencia
and	b.nr_atendimento  = nr_atendimento_p
and	b.dt_prescricao = (SELECT max(b.dt_prescricao)
			 from	PE_PRESCR_DIAG a,
				PE_PRESCRICAO b
			where 	a.nr_seq_prescr = b.nr_sequencia
			and	b.nr_atendimento  = nr_atendimento_p);

return	qt_reg_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
