SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_reg_estornado_tpb ( nr_bordero_p bigint, nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


qt_registros_w	bigint := 0;


BEGIN
if (nr_bordero_p IS NOT NULL AND nr_bordero_p::text <> '') and (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

begin

	select	count(*)
	into STRICT	qt_registros_w
	from	titulo_pagar_baixa a
	where	a.nr_seq_baixa_origem in (	SELECT	x.nr_sequencia
						from	titulo_pagar_baixa x
						where	x.nr_bordero	= nr_bordero_p
						and	x.nr_titulo	= nr_titulo_p)
	and	a.nr_titulo	= nr_titulo_p;
end;

end if;

return	qt_registros_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
