SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_reg_wpage_control ( nr_seq_item_pasta_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_dic_obj_w bigint;
qt_reg_config_comp_w bigint;


BEGIN
qt_reg_config_comp_w := 0;

if (nr_seq_item_pasta_p > 0)  then
	SELECT  coalesce(max(z.NR_SEQ_DIC_OBJ),0)
	into STRICT	nr_seq_dic_obj_w
	FROM	prontuario_pasta z
	WHERE	z.nr_sequencia IN (obter_seq_pasta_superior(nr_seq_item_pasta_p, 'P'));

	if (nr_seq_dic_obj_w > 0) then

		SELECT 	COUNT(*)
		into STRICT 	qt_reg_config_comp_w
		FROM   	CONFIG_WPAGE_CONTROL
		WHERE  	NR_SEQ_OBJETO = nr_seq_dic_obj_w;

	end if;
end if;

return	qt_reg_config_comp_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_reg_wpage_control ( nr_seq_item_pasta_p bigint) FROM PUBLIC;

