CREATE OR REPLACE FUNCTION obter_qt_reinternacao_pac ( cd_pessoa_fisica_p text, cd_medico_p text, nr_atendimento_p bigint, dt_alta_p timestamp) RETURNS bigint AS $body$
DECLARE

 
vl_retorno_w		integer;
			

BEGIN 
 
select	CASE WHEN count(*)=0 THEN 0  ELSE 1 END  
into STRICT	vl_retorno_w 
from	atendimento_paciente 
where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	cd_medico_resp		= cd_medico_p 
and	dt_entrada between dt_alta_p and dt_alta_p + 15 
and	nr_atendimento		<> nr_atendimento_p 
and	ie_tipo_atendimento	= 1;
 
return	vl_Retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
