SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_rel_os (nr_seq_grupo_p bigint, cd_pessoa_solicitante_p text, cd_funcao_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, qt_espaco_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_os_w			bigint;
qt_concluidas_w		bigint;
qt_porcentagem_w	varchar(50);
qt_outro_per_w		bigint;
ds_retorno_w		varchar(4000);
qt_divisor_w		bigint;


BEGIN
if (ie_opcao_p = 'TG') then
	SELECT COUNT(*)
	into STRICT	qt_os_w
	FROM   man_ordem_servico f,
		Man_ordem_serv_estagio v
	WHERE  f.DT_ORDEM_SERVICO BETWEEN dt_inicio_p AND dt_fim_p
	AND    f.NR_SEQ_EQUIPAMENTO = 2897
	AND	f.nr_sequencia = v.nr_seq_ordem
	;

	SELECT COUNT(*)
	into STRICT	qt_concluidas_w
	FROM   man_ordem_servico f,
		Man_ordem_serv_estagio v
	WHERE  DT_ORDEM_SERVICO BETWEEN dt_inicio_p AND dt_fim_p
	AND    v.NR_SEQ_ESTAGIO IN (791, 1234)
	AND    NR_SEQ_EQUIPAMENTO = 2897
	AND    v.dt_atualizacao BETWEEN dt_inicio_p AND dt_fim_p
	and	f.nr_sequencia = v.nr_seq_ordem;

	SELECT 	COUNT(*)
	into STRICT	qt_outro_per_w
	FROM   	man_ordem_servico f,
		Man_ordem_serv_estagio v
	WHERE   v.nr_seq_ordem = f.nr_sequencia
	AND  	v.NR_SEQ_ESTAGIO IN (791, 1234)
	AND    	f.nr_seq_equipamento = 2897
	AND 	 NOT(DT_ORDEM_SERVICO BETWEEN dt_inicio_p AND dt_fim_p)
	AND 	 v.dt_atualizacao BETWEEN dt_inicio_p AND dt_fim_p;

	ds_retorno_w := qt_concluidas_w + qt_os_w + qt_outro_per_w;

	return ds_retorno_w;
elsif ( ie_opcao_p = 'TU') then

	SELECT COUNT(*)
	INTO STRICT	qt_os_w
	FROM   man_ordem_servico f,
		Man_ordem_serv_estagio v
	WHERE  f.DT_ORDEM_SERVICO BETWEEN dt_inicio_p AND dt_fim_p
	AND	   f.nr_sequencia = v.nr_seq_ordem
	AND    f.NR_SEQ_EQUIPAMENTO = 2897
	AND	f.cd_pessoa_solicitante = cd_pessoa_solicitante_p
	AND	coalesce(f.cd_funcao,0) = cd_funcao_p
	AND	coalesce(OBTER_GERENCIA_GRUPO_DESEN(nr_seq_grupo_des, 'C'),0) = nr_seq_grupo_p;

	SELECT COUNT(*)
	INTO STRICT   qt_concluidas_w
	FROM   man_ordem_servico f,
		   Man_ordem_serv_estagio v
	WHERE  DT_ORDEM_SERVICO BETWEEN dt_inicio_p AND dt_fim_p
	AND    NR_SEQ_EQUIPAMENTO = 2897
	AND    v.NR_SEQ_ESTAGIO IN (791, 1234)
	AND    v.dt_atualizacao BETWEEN dt_inicio_p AND dt_fim_p
	AND	   f.nr_sequencia = v.nr_seq_ordem
	AND	   f.cd_pessoa_solicitante = cd_pessoa_solicitante_p
	AND	   coalesce(f.cd_funcao,0) = cd_funcao_p
	AND	   coalesce(OBTER_GERENCIA_GRUPO_DESEN(nr_seq_grupo_des, 'C'),0) = nr_seq_grupo_p;

	SELECT 	COUNT(*)
	into STRICT	qt_outro_per_w
	FROM   	man_ordem_servico f,
		Man_ordem_serv_estagio v
	WHERE    v.nr_seq_ordem = f.nr_sequencia
	AND  	NOT(DT_ORDEM_SERVICO BETWEEN dt_inicio_p AND dt_fim_p)
	AND    	f.nr_seq_equipamento = 2897
	AND	v.NR_SEQ_ESTAGIO IN (791, 1234)
	AND 	v.dt_atualizacao BETWEEN dt_inicio_p AND dt_fim_p
	and	f.cd_pessoa_solicitante = cd_pessoa_solicitante_p
	and	coalesce(f.cd_funcao,0) = cd_funcao_p
	and	coalesce(OBTER_GERENCIA_GRUPO_DESEN(nr_seq_grupo_des, 'C'),0) = nr_seq_grupo_p;

	select 	CASE WHEN qt_os_w=0 THEN  1  ELSE qt_os_w END
	into STRICT	qt_divisor_w
	;

	qt_porcentagem_w := qt_concluidas_w*100/qt_divisor_w||'%';

	ds_retorno_w := qt_os_w || qt_espaco_p || qt_concluidas_w || qt_espaco_p || qt_porcentagem_w || qt_espaco_p || qt_outro_per_w;

	return	ds_retorno_w;

elsif (ie_opcao_p = 'QTG') then

	SELECT COUNT(*)
	into STRICT	qt_os_w
	FROM   man_ordem_servico f,
		   Man_ordem_serv_estagio v
	WHERE  f.DT_ORDEM_SERVICO BETWEEN dt_inicio_p AND dt_fim_p
	and 	v.nr_seq_ordem = f.nr_sequencia
	AND    f.NR_SEQ_EQUIPAMENTO = 2897
	and	coalesce(OBTER_GERENCIA_GRUPO_DESEN(nr_seq_grupo_des, 'C'),0) = nr_seq_grupo_p;


	SELECT COUNT(*)
	into STRICT	qt_concluidas_w
	FROM   man_ordem_servico f,
		Man_ordem_serv_estagio v
	WHERE  DT_ORDEM_SERVICO BETWEEN dt_inicio_p AND dt_fim_p
	AND    NR_SEQ_EQUIPAMENTO = 2897
	AND    v.NR_SEQ_ESTAGIO IN (791, 1234)
	AND    v.dt_atualizacao BETWEEN dt_inicio_p AND dt_fim_p
	and	f.nr_sequencia = v.nr_seq_ordem
	and	coalesce(OBTER_GERENCIA_GRUPO_DESEN(nr_seq_grupo_des, 'C'),0) = nr_seq_grupo_p;

	SELECT 	COUNT(*)
	into STRICT	qt_outro_per_w
	FROM   	man_ordem_servico f,
		Man_ordem_serv_estagio v
	WHERE    v.nr_seq_ordem = f.nr_sequencia
	AND  	NOT(DT_ORDEM_SERVICO BETWEEN dt_inicio_p AND dt_fim_p)
	AND    	f.nr_seq_equipamento = 2897
	AND	v.NR_SEQ_ESTAGIO IN (791, 1234)
	AND 	 v.dt_atualizacao BETWEEN dt_inicio_p AND dt_fim_p
	and	coalesce(OBTER_GERENCIA_GRUPO_DESEN(nr_seq_grupo_des, 'C'),0) = nr_seq_grupo_p;

	ds_retorno_w := qt_concluidas_w+qt_os_w+qt_outro_per_w;

	return ds_retorno_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_rel_os (nr_seq_grupo_p bigint, cd_pessoa_solicitante_p text, cd_funcao_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, qt_espaco_p text, ie_opcao_p text) FROM PUBLIC;

