CREATE OR REPLACE FUNCTION obter_qt_reprov_ordem_oci ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_retorno_w				double precision;


BEGIN 
 
select	coalesce(sum(a.qt_prevista_entrega),0) 
into STRICT	qt_retorno_w 
from	ordem_compra c, 
	ordem_compra_item b, 
	ordem_compra_item_entrega a 
where	a.nr_ordem_compra = nr_ordem_compra_p 
and	c.nr_ordem_compra = b.nr_ordem_compra 
and	a.nr_item_oci = nr_item_oci_p 
and	a.nr_ordem_compra = b.nr_ordem_compra 
and	a.nr_item_oci = b.nr_item_oci 
and	(b.dt_reprovacao IS NOT NULL AND b.dt_reprovacao::text <> '');
 
return qt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
